const e=document.querySelector("#mobile-menu"),s=document.querySelector("#menu-btn"),n=document.querySelector("#cross-btn"),c=document.querySelector("#mobile-services-btn"),l=document.querySelector("#mobile-submenu");s.addEventListener("click",()=>{e.classList.remove("translate-x-full"),e.classList.add("translate-x-0"),s.classList.add("hidden")});n.addEventListener("click",()=>{e.classList.add("translate-x-full"),s.classList.remove("hidden")});c.addEventListener("click",()=>{l.classList.toggle("hidden")});document.addEventListener("scroll",()=>{const t=document.querySelector("header");window.scrollY>50?t.classList.add("scroll-nav"):t.classList.remove("scroll-nav")});
